@bot.message_handler(commands=["start"])
def start(message):
    main_menu(message)

def main_menu(message):
    markup = telebot.types.ReplyKeyboardMarkup(row_width=2, resize_keyboard=True)
    buttons = ["–ù–∞—à —Å–∞–π—Ç üåê", "–ò–Ω—Å—Ç—Ä—É–∫—Ü–∏–∏ –ø–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—é –ø—Ä–æ–¥—É–∫—Ç–æ–≤ üìñ", "–ù–∞–ø–∏—à–∏—Ç–µ –Ω–∞–º ‚úâÔ∏è"]
    markup.add(*[telebot.types.KeyboardButton(btn) for btn in buttons])
    bot.send_message(message.chat.id, "üåø –î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å! –í—ã–±–µ—Ä–∏—Ç–µ –¥–µ–π—Å—Ç–≤–∏–µ:", reply_markup=markup)

